    #[case::fail_short_input("", "", true, 12000)]
    #[case::fail_large_input("", "", true, 12000)]
    #[case::fail_violate_top_bytes("", "", true, 12000)]
    #[case::fail_invalid_field_element("", "", true, 12000)]
    #[case::fail_point_not_on_curve("", "", true, 12000)]
    fn test_g1_mul(
        #[case] input: &str,
        #[case] expected_output: &str,
        #[case] expected_error: bool,
        #[case] expected_gas: u64,
    ) {
        let input = Bytes::from_hex(input).unwrap();
        let target_gas: u64 = 30_000_000;
        let res = g1_mul(&input, target_gas);
        if expected_error {
            assert!(res.is_err());
        } else {
            let (actual_gas, actual_output) = res.unwrap();
            assert_eq!(expected_gas, actual_gas);
            let expected_output = Bytes::from_hex(expected_output).unwrap();
            assert_eq!(expected_output, actual_output);
        }
    }
